// import { Component } from "react";
import { useRef } from "react";
import { useSelector, useDispatch } from "react-redux";
import classes from "./Counter.module.css";

const Counter = () => {
  let increase = useRef(0);
  let decrease = useRef(0);
  const dispatch = useDispatch();
  const counter = useSelector((state) => state.counter);
  const show = useSelector((state) => state.showCounter);

  const incrementHandler = () => {
    dispatch({ type: "increment" });
  };

  const increaseHandler = () => {
    // console.log(parseInt(amount.current.value));
    dispatch({ type: "increase", amount: parseInt(increase.current.value) });
  };

  const decreaseHandler = () => {
    // console.log(parseInt(amount.current.value));
    dispatch({ type: "decrease", amount: parseInt(decrease.current.value) });
  };

  const decrementHandler = () => {
    dispatch({ type: "decrement" });
  };

  const toggleCounterHandler = () => {
    dispatch({ type: "toggle" });
  };

  return (
    <main className={classes.counter}>
      <h1>Redux Counter</h1>
      {show && <div className={classes.value}>{counter}</div>}
      <button onClick={incrementHandler}>increment</button>
      <button onClick={decrementHandler}>decrement</button>
      <section>
        <button onClick={increaseHandler}>Increase By</button>
        <input className={classes.input} type="number" ref={increase} />
      </section>
      <section>
        <button onClick={decreaseHandler}>Decrease By</button>
        <input className={classes.input} type="number" ref={decrease} />
      </section>
      <button onClick={toggleCounterHandler}>Toggle Counter</button>
    </main>
  );
};

export default Counter;

