import { createSlice } from "@reduxjs/toolkit";

const initualCartState = { view: false };
const cartSlice = createSlice({
  name: "cart",
  initialState: initualCartState,
  reducers: {
    viewCart(state) {
      state.view = !state.view;
    },
  },
});
export const counterActions = cartSlice.actions;
