import React, {
  useState,
  useEffect,
  useReducer,
  useContext,
  useRef,
} from "react";

import Card from "../UI/Card/Card";
import classes from "./Login.module.css";
import AuthContext from "../store/auth-context";
import LoginJSX from "./LoginJSX";
import Button from "../UI/Button/Button";

const allinonefunction = (state, action) => {
  if (action.type === "USER_INPUT_EMAIL") {
    return {
      valueEmail: action.val,
      isValidEmail: action.val.includes("@"),
    };
  }

  if (action.type === "INPUT_BLUR_EMAIL") {
    return { valueEmail: state.val, isValidEmail: state.val.includes("@") };
  }
  if (action.type === "USER_INPUT_PASSWORD") {
    return {
      valuePassword: action.value,
      isValidPassword: action.value.trim().length > 7,
    };
  }

  if (action.type === "USER_BLUR_PASSWORD") {
    return {
      valuePassword: state.value,
      isValidPassword: state.value.trim().length > 7,
    };
  }

  return {
    valueEmail: "",
    valuePassword: "",
    isValidEmail: null,
    isValidPassword: null,
  };
};

const Login = () => {
  // const [enteredEmail, setEnteredEmail] = useState("");
  // const [emailIsValid, setEmailIsValid] = useState();
  // const [enteredPassword, setEnteredPassword] = useState("");
  // const [passwordIsValid, setPasswordIsValid] = useState();
  const [formIsValid, setFormIsValid] = useState(false);

  const [allinone, dispatchallinone] = useReducer(allinonefunction, {
    valueEmail: "",
    valuePassword: "",
    isValidEmail: null,
    isValidPassword: null,
  });

  const authCtx = useContext(AuthContext);

  const emailInputRef = useRef();
  const passwordInputRef = useRef();

  useEffect(() => {
    console.log("Effect RUNNING");

    return () => {
      console.log("Effect CleanUp");
    };
  }, []);

  const { isValidEmail: emailIsValid } = allinone;

  const { isValidPassword: passwordIsValid } = allinone;

  useEffect(() => {
    const identifier = setTimeout(() => {
      console.log("Checking for validity!");
      setFormIsValid(passwordIsValid && passwordIsValid);
    }, 500);

    return () => {
      console.log("Cleanup!");
      clearTimeout(identifier);
    };
  }, [emailIsValid, passwordIsValid]);

  const emailChangeHandler = (event) => {
    // setEnteredEmail(event.target.value);
    dispatchallinone({ type: "USER_INPUT_EMAIL", val: event.target.value });

    // setFormIsValid(event.target.value.includes("@") && passwordState.isValid);
  };

  const passwordChangeHandler = (event) => {
    // setEnteredPassword(event.target.value);
    dispatchallinone({
      type: "USER_INPUT_PASSWORD",
      value: event.target.value,
    });

    // setFormIsValid(emailState.isValid && event.target.value.trim().length > 6);
  };

  const validateEmailHandler = () => {
    // setEmailIsValid(emailState.isValid);
    dispatchallinone({ type: "INPUT_BLUR_EMAIL" });
  };

  const validatePasswordHandler = () => {
    // setPasswordIsValid(passwoed.isValid);
    dispatchallinone({ type: "INPUT_BLUR_PASSWORD" });
  };

  const submitHandler = (event) => {
    event.preventDefault();
    if (formIsValid)
      authCtx.onLogin(allinone.valueEmail, allinone.valuePassword);
    else if (!emailIsValid) {
      emailInputRef.current.focus();
    } else if (!passwordIsValid) {
      passwordInputRef.current.focus();
    }
  };

  return (
    <Card className={classes.login}>
      <form onSubmit={submitHandler}>
        <LoginJSX
          ref={emailInputRef}
          type="email"
          id="email"
          label="E-Mail"
          isValid={emailIsValid}
          value={allinone.valueEmail}
          onChange={emailChangeHandler}
          onBlur={validateEmailHandler}
        />
        <LoginJSX
          ref={passwordInputRef}
          type="password"
          id="password"
          label="Password"
          isValid={passwordIsValid}
          value={allinone.valuePassword}
          onChange={passwordChangeHandler}
          onBlur={validatePasswordHandler}
        />
        <div className={classes.actions}>
          <Button type="submit" className={classes.btn}>
            Login
          </Button>
        </div>
      </form>
    </Card>
  );
};

export default Login;
