import React, { useReducer } from "react";

const initialTodos = [
  {
    items: [
      {
        id: 1,
        title: "Test1",
        price: 6,
        quantity: 2,
        totalPrice: 12,
        description: "This is a first product - amazing!",
      },
      {
        id: 2,
        title: "Test2",
        price: 8,
        quantity: 2,
        totalPrice: 16,
        description: "This is a Second product - Dumb!",
      },
    ],
    totalQuantity: 0,
  },
];

const Reducer = (state, action) => {
  switch (action.type) {
    case "addItemToCart":
      const newItem = action.item;
      const existingItem = state.items.findIndex(
        (item) => item.id === newItem.id
      );
      const existingCartItem = state.items[existingItem];
      let updatedItems;
      if (!existingItem) {
        updatedItems = state.items.concat(action.item);
      } else {
        const updatedItem = {
          ...existingCartItem,
          quantity: existingCartItem.quantity + 1,
          totalPrice: existingItem.totalPrice + newItem.price,
        };
        updatedItems = [...state.items];
        updatedItems[existingCartItem] = updatedItem;
      }
      const updatedTotalQuantity = state.totalQuantity + 1;
      return { items: updatedItems, totalQuantity: updatedTotalQuantity };

    case "removeItemFromCart":
      const existingItemIndex1 = state.items.findIndex(
        (item) => item.id === action.id
      );
      const existingItem1 = state.items[existingItemIndex1];
      if (existingItem1.quantity === 1) {
        updatedItems = state.items.filter((item) => item.id !== action.id);
      } else {
        const updatedItem = {
          ...existingItem1,
          quantity: existingItem1.quantity - 1,
          totalPrice: existingItem1.totalprice - existingItem1.price,
        };
        updatedItems = [...state.items];
        updatedItem[existingItemIndex1] = updatedItem;
      }
      const updatedTotalQuantity1 = state.totalQuantity - 1;
      return { items: updatedItems, totalQuantity: updatedTotalQuantity1 };

    default:
      return initialTodos;
  }
};

const AuthContext = React.createContext({
  showCart: true,
  MyCart: [
    {
      id: 1,
      title: "Test1",
      price: 6,
      quantity: 2,
      totalPrice: 12,
      description: "This is a first product - amazing!",
    },
    {
      id: 2,
      title: "Test2",
      price: 8,
      quantity: 2,
      totalPrice: 16,
      description: "This is a Second product - Dumb!",
    },
  ],
  totalQuantity: 0,
  addItemToCart: (item) => {},
  removeItemFromCart: (item) => {},
});

export const AuthContextProvider = (props) => {
  const [Item, dispatchItem] = useReducer(Reducer, initialTodos);

  const addItemToCart = (item) => {
    dispatchItem({
      type: "addItemToCart",
      item: item,
    });
  };

  const removeItemFromCart = (item) => {
    dispatchItem({ type: "removeItemFromCart", id: item.id });
  };

  return (
    <AuthContext.Provider
      value={{
        showCart: true,
        MyCart: [Item.items],
        totalQuantity: Item.totalQuantity,
        addItemToCart: addItemToCart,
        removeItemFromCart: removeItemFromCart,
      }}
    >
      {props.children}
    </AuthContext.Provider>
  );
};

export default AuthContext;
