import React, { useState, useReducer } from "react";

const initialTodos = [
  {
    items: [],
    totalQuantity: 0,
  },
];

const Reducer = (state, action) => {
  switch (action.type) {
    case "addItemToCart":
      const newItem = action.item;
      const existingItem = state.items.findIndex(
        (item) => item.id === newItem.id
      );
      if (!existingItem) {
        state.items.push({
          id: newItem.id,
          price: newItem.price,
          quantity: 1,
          totalPrice: newItem.price,
          name: newItem.title,
        });
      } else {
        existingItem.quantity = existingItem.quantity + 1;
        existingItem.totalPrice = existingItem.totalPrice + newItem.price;
      }
      state.totalQuantity = state.totalQuantity + 1;
      return initialTodos;

    case "removeItemFromCart":
      const id = action.payload;
      const existingItem1 = state.items.findIndex((item) => item.id === id);
      if (existingItem1 === 1) {
        state.items = state.items.filter((item) => item.id !== id);
      } else {
        existingItem1.quantity = existingItem1.quantity - 1;
        existingItem1.totalPrice =
          existingItem1.totalprice - existingItem1.price;
      }
      state.totalQuantity = state.totalQuantity - 1;
      return initialTodos;

    default:
      return state;
  }
};

const AuthContext = React.createContext({
  showCart: false,
  MyCart: [],
  totalQuantity: 0,
  changeShowCart: () => {},
  addItemToCart: (item) => {},
  removeItemFromCart: (item) => {},
});

export const AuthContextProvider = (props) => {
  const [showCart, setShowCart] = useState();
  const [Item, dispatchItem] = useReducer(Reducer, initialTodos);

  const showCartHandler = () => {
    console.log(showCart);
    setShowCart(!showCart);
  };

  const addItemToCart = (item) => {
    dispatchItem({
      type: "addItemToCart",
      id: item.id,
      title: item.title,
      price: item.price,
      description: item.description,
    });
  };

  const removeItemFromCart = (item) => {
    dispatchItem({ type: "removeItemFromCart", id: item.id });
  };

  return (
    <AuthContext.Provider
      value={{
        showCart: showCart,
        MyCart: [Item],
        totalQuantity: 0,
        changeShowCart: showCartHandler,
        addItemToCart: addItemToCart,
        removeItemFromCart: removeItemFromCart,
      }}
    >
      {props.children}
    </AuthContext.Provider>
  );
};

export default AuthContext;
