import React from "react";

const AuthContext = React.createContext({
  showCart: false,
  MyCart: [],
  cartItems: 0,
});

export const AuthContextProvider = (props) => {
  <AuthContext.Provider
    value={{
      showCart: showCart,
      MyCart: [],
      cartItems: 0,
    }}
  >
    {props.children}
  </AuthContext.Provider>;
};

export default AuthContext;
